const util = require('ethereumjs-util');
const fs = require('fs');

const CREATION_CODE = '';

const deployer = "0x63f765e3df3eaaa39eb4b7f1241b500380e1d751"
const nonce = 1
const contractAddress = util.generateAddress(deployer, nonce.toString());
const internalDeployer = util.generateAddress(contractAddress, '1');

const makeWallet = address => {
  const code = `${CREATION_CODE}000000000000000000000000${util.bufferToHex(contractAddress).substr(2)}000000000000000000000000${address.substr(2)}`
  return util.bufferToHex(util.generateAddress2(internalDeployer, util.setLengthLeft('0x0', 32), code));
}

function Create2() {
  this.addInput("address","string")
  this.addOutput("address","string")
  this.size[0] = 210
  this.value = null
}

Create2.title = "Create2";

Create2.prototype.onExecute = function() {
  let address = this.getInputData(0)
  if(typeof address != "undefined" && address!=this.address){
    this.address = address
    this.value = makeWallet(address)
  }

  this.setOutputData(0,this.value)
};


export default Create2
